package javapack;

public class mission4_stringreversal {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String a="apppppaal";
		int i;
		int l=a.length();
		System.out.println(l);
		for( i=l-1;i>=0;i--)
		{
			System.out.print(a.charAt(i));
		}

	}

}
