import java.util.Scanner;
public class program_78_following_output 
{

	public static void main(String[] args) 
	{
		int i,j,num;
		for(i=0;i<6;i++)
		{
			num=1;
			for(j=0;j<i;j++)
			{
				System.out.print(num+ "");
				num++;
				
			}
			System.out.println();
			
		}
		// TODO Auto-generated method stub

	}

}
